mapper.select:
select * from movie where movie_id in (
    select movie_id from (
        select count(*) as c,movie_id from comment group by movie_id
    ) m order by c
) and region_id = #{regionId} and status != -1 limit 10

select * from movie where movie_id in (
    select movie_id from movie_actor where actor_id = #{actorId}
  ) and status != -1 order by  rate limit 2;

SELECT distinct movie.* FROM (SELECT movie_id FROM movie_type WHERE type_id IN
#{type.typeId}
) test left JOIN movie ON movie.movie_id = test.movie_id where movie.status != -1 ORDER BY rate DESC limit 20

select * from movie


    and  LOCATE(#{vo.name},`name`) > 0 OR LOCATE(#{vo.name},`alias`) > 0



    and region_id in

          #{re}



    and movie_id in
    (
        select movie_id from movie_type where type_id in

              #{ty}

    )


  AND release_date >= #{vo.earlyDate,jdbcType=DATE}



  AND release_date <= #{vo.latedDate,jdbcType=DATE}


  and rate >= #{vo.minScore}


  and rate <= #{vo.maxScore}

select * from movie where movie_id in (
    select movie_id from movie_actor where actor_id = #{actorId}
  )
